import { useState } from 'react';
import { Link, Route, BrowserRouter as Router, Routes, useNavigate } from 'react-router-dom';
import './App.css';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import BlogDetail from './components/Blog/BlogDetail';
import BlogForm from './components/Blog/BlogForm';
import BlogList from './components/Blog/BlogList';
import ProfileView from './components/Profile/ProfileView';

function AppContent() {
  const [search, setSearch] = useState('');
  const navigate = useNavigate();
  const isLoggedIn = !!localStorage.getItem('token');

  const handleCategoryClick = (category) => {
    navigate(`/category/${category}`);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (search.trim()) {
      navigate(`/search/${search.trim()}`);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <>
      <header className="header" style={{ background: 'linear-gradient(90deg, #6a11cb 0%, #2575fc 100%)', color: 'white', padding: '1rem 2rem', marginBottom: '2rem' }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', maxWidth: '1200px', margin: '0 auto', width: '100%' }}>
          <div style={{ fontWeight: 'bold', fontSize: '2rem', letterSpacing: '1px' }}>
            <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>SideBlogger</Link>
          </div>
          <nav className="nav-links" style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
            <Link to="/">Home</Link>
            <Link to="/create">Create Post</Link>
            <Link to="/profile">Profile</Link>
            {!isLoggedIn && <Link to="/login">Login</Link>}
            {!isLoggedIn && <Link to="/register">Register</Link>}
            {isLoggedIn && <button onClick={handleLogout} style={{ background: 'transparent', color: 'white', border: 'none', fontWeight: 'bold', cursor: 'pointer' }}>Logout</button>}
            <button onClick={() => handleCategoryClick('Tech')} style={{ background: 'transparent', color: 'white', border: 'none', fontWeight: 'bold', cursor: 'pointer' }}>Tech</button>
            <button onClick={() => handleCategoryClick('Lifestyle')} style={{ background: 'transparent', color: 'white', border: 'none', fontWeight: 'bold', cursor: 'pointer' }}>Lifestyle</button>
            <button onClick={() => handleCategoryClick('Education')} style={{ background: 'transparent', color: 'white', border: 'none', fontWeight: 'bold', cursor: 'pointer' }}>Education</button>
            <button onClick={() => handleCategoryClick('Business')} style={{ background: 'transparent', color: 'white', border: 'none', fontWeight: 'bold', cursor: 'pointer' }}>Business</button>
            <form onSubmit={handleSearch} style={{ display: 'inline' }}>
              <input type="text" value={search} onChange={e => setSearch(e.target.value)} placeholder="Search..." style={{ padding: '0.5rem', borderRadius: '4px', border: 'none', marginLeft: '1rem' }} />
            </form>
          </nav>
        </div>
      </header>
      <Routes>
        <Route path="/" element={<BlogList />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/create" element={<BlogForm />} />
        <Route path="/edit/:id" element={<BlogForm />} />
        <Route path="/post/:id" element={<BlogDetail />} />
        <Route path="/profile" element={<ProfileView />} />
        {/* Add category and search routes here for future expansion */}
      </Routes>
    </>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
