import axios from 'axios';
import { useEffect, useState } from 'react';
import { likePost } from '../../utils/api';

const API_URL = 'http://localhost:5000/api/posts';

const BlogList = () => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [userName, setUserName] = useState('');
    const [userId, setUserId] = useState('');

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await axios.get(API_URL);
                setPosts(response.data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };
        fetchPosts();
        // Fetch logged-in user profile
                import('../../utils/api').then(api => {
                        api.fetchUserProfile().then(profile => {
                                // Prefer profile.name, then user.name, then user.username, then email, then fallback
                                let name = '';
                                if (profile) {
                                    if (profile.profile && profile.profile.name) {
                                        name = profile.profile.name;
                                    } else if (profile.name) {
                                        name = profile.name;
                                    } else if (profile.user && profile.user.name) {
                                        name = profile.user.name;
                                    } else if (profile.user && profile.user.username) {
                                        name = profile.user.username;
                                    } else if (profile.user && profile.user.email) {
                                        name = profile.user.email.split('@')[0];
                                    }
                                }
                                setUserName(name || 'User');
                                setUserId((profile.user && profile.user._id) || (profile.profile && profile.profile.user) || '');
                        }).catch(() => {
                                setUserName('User');
                                setUserId('');
                        });
                });
    }, []);

    if (loading) return <div className="container">Loading...</div>;
    if (error) return <div className="container">Error: {error}</div>;

    return (
    <div className="blog-main-layout" style={{ display: 'flex', gap: '2rem', maxWidth: '1200px', margin: '2rem auto', background: '#f6f8fa' }}>
            {/* Main Content */}
            <div style={{ flex: 2 }}>
                {/* Welcome Card (moved to top) */}
                <div style={{ background: '#7b6cf6', color: 'white', borderRadius: '16px', padding: '2rem', marginBottom: '2rem', boxShadow: '0 2px 12px rgba(0,0,0,0.07)', textAlign: 'center' }}>
                    <div style={{ fontWeight: 'bold', fontSize: '1.5rem', marginBottom: '0.5rem' }}>üëã Welcome back, {userName}!</div>
                    <div style={{ marginBottom: '1.2rem', fontSize: '1.1rem' }}>Ready to share your next big idea with the community?</div>
                    <a href="/create" style={{ display: 'inline-block', background: 'white', color: '#7b6cf6', borderRadius: '8px', padding: '0.75rem 2rem', textAlign: 'center', fontWeight: 'bold', fontSize: '1.1rem', textDecoration: 'none', boxShadow: '0 1px 4px rgba(44,62,80,0.08)' }}>+ Write New Post</a>
                </div>
                <h1 style={{ fontSize: '2rem', fontWeight: 'bold', marginBottom: '1.5rem', color: '#3a3a3a' }}>Latest Posts</h1>
                {/* Search Bar */}
                <div style={{ marginBottom: '2rem' }}>
                    <input type="text" placeholder="Search posts, authors, or tags..." style={{ width: '100%', padding: '0.75rem 1.2rem', borderRadius: '24px', border: '1px solid #d1d5db', fontSize: '1rem', background: '#fff' }} />
                </div>
                {/* Post Cards */}
                {posts.map(post => (
                    <div key={post._id} style={{ background: '#fff', borderRadius: '12px', boxShadow: '0 2px 12px rgba(0,0,0,0.07)', padding: '1.5rem 2rem', marginBottom: '2rem', display: 'flex', flexDirection: 'column' }}>
                        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '0.5rem' }}>
                            <div style={{ background: '#6c63ff', color: 'white', borderRadius: '50%', width: 38, height: 38, display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: '1.1rem', marginRight: '1rem' }}>
                                {post.author?.name ? post.author.name[0].toUpperCase() : 'U'}
                            </div>
                            <div>
                                <div style={{ fontWeight: 'bold', color: '#3a3a3a' }}>{post.author?.name || 'Unknown'}</div>
                                <div style={{ fontSize: '0.95rem', color: '#888' }}>{new Date(post.createdAt).toLocaleDateString()}</div>
                            </div>
                        </div>
                        <div style={{ fontSize: '1.25rem', fontWeight: 'bold', margin: '0.5rem 0 0.5rem 0', color: '#222' }}>{post.title}</div>
                        <div style={{ fontSize: '1rem', color: '#444', marginBottom: '1rem' }}>{post.content}</div>
                        <div style={{ marginBottom: '1rem' }}>
                            {post.tags && post.tags.map((tag, idx) => (
                                <span key={idx} style={{ background: '#f1f3f6', color: '#6c63ff', borderRadius: '16px', padding: '0.3rem 0.9rem', fontSize: '0.95rem', marginRight: '0.5rem', fontWeight: 'bold', border: '1px solid #e0e0e0' }}>#{tag}</span>
                            ))}
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', gap: '1rem', marginTop: '0.5rem' }}>
                            <div style={{ display: 'flex', alignItems: 'center', gap: '1.2rem' }}>
                                <button
                                    onClick={async () => {
                                        const token = localStorage.getItem('token');
                                        if (!token) return;
                                        const res = await likePost(post._id, token);
                                        setPosts(posts => posts.map(p => p._id === post._id ? { ...p, likes: res.likes } : p));
                                    }}
                                    style={{ background: '#fff', color: '#6c63ff', border: '1px solid #6c63ff', borderRadius: '50%', width: 36, height: 36, display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: '1.1rem', cursor: 'pointer' }}
                                    title="Like"
                                >‚ù§</button>
                                <span style={{ color: '#6c63ff', fontWeight: 'bold' }}>{post.likes ? post.likes.length : 0} Likes</span>
                                <span style={{ color: '#888', fontWeight: 'bold' }}>{post.views || 0} Views</span>
                            </div>
                            <div style={{ display: 'flex', gap: '1rem' }}>
                                <a href={`/post/${post._id}`} style={{ background: '#6c63ff', color: 'white', padding: '0.5rem 1.5rem', borderRadius: '24px', textDecoration: 'none', fontWeight: 'bold', fontSize: '1rem', boxShadow: '0 1px 4px rgba(44,62,80,0.08)' }}>Read More ‚Üí</a>
                                {post.author?._id === userId && (
                                    <button
                                        onClick={async () => {
                                            const token = localStorage.getItem('token');
                                            await import('../../utils/api').then(api => api.deletePost(post._id, token));
                                            // Refresh posts after delete
                                            setPosts(posts.filter(p => p._id !== post._id));
                                        }}
                                        style={{ background: '#ff4d4f', color: 'white', border: 'none', borderRadius: '24px', padding: '0.5rem 1.2rem', fontWeight: 'bold', fontSize: '1rem', cursor: 'pointer' }}
                                    >Delete</button>
                                )}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            {/* Sidebar */}
            <div style={{ flex: 1, display: 'flex', flexDirection: 'column', gap: '2rem' }}>
                <div style={{ background: '#fff', color: '#3a3a3a', borderRadius: '12px', padding: '2rem', boxShadow: '0 2px 12px rgba(0,0,0,0.07)' }}>
                    <div style={{ fontWeight: 'bold', fontSize: '1.15rem', marginBottom: '1rem' }}>Community Stats</div>
                    <div style={{ fontSize: '1rem', marginBottom: '0.7rem' }}><span style={{ fontWeight: 'bold' }}>{posts.length}</span> Total Posts</div>
                    <div style={{ fontSize: '1rem', marginBottom: '0.7rem' }}><span style={{ fontWeight: 'bold' }}>{posts.reduce((acc, p) => acc + (p.views || 0), 0)}</span> Total Views</div>
                    <div style={{ fontSize: '1rem' }}><span style={{ fontWeight: 'bold' }}>{[...new Set(posts.map(p => p.author?.name || 'Unknown'))].length}</span> Authors</div>
                </div>
            </div>
        </div>
    );
};

export default BlogList;
