import axios from 'axios';
import { useEffect, useState } from 'react';

const API_URL = 'http://localhost:5000/api/posts';

const BlogList = () => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await axios.get(API_URL);
                setPosts(response.data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };
        fetchPosts();
    }, []);

    if (loading) return <div className="container">Loading...</div>;
    if (error) return <div className="container">Error: {error}</div>;

    return (
        <div className="container magazine-layout">
            <h1 className="magazine-title">Latest & Trending</h1>
            <div className="magazine-grid">
                {posts.map(post => (
                    <div className="magazine-card" key={post._id}>
                        {post.image ? (
                            <img src={`http://localhost:5000${post.image}`} alt={post.title} className="magazine-img" />
                        ) : (
                            <div className="magazine-img magazine-img-placeholder">No Image</div>
                        )}
                        <div className="magazine-content">
                            <div className="magazine-title">{post.title}</div>
                            <div className="magazine-meta">By {post.author?.name || 'Unknown'} | {new Date(post.createdAt).toLocaleDateString()}</div>
                            <p className="magazine-excerpt">{post.content.substring(0, 120)}...</p>
                            <a href={`/post/${post._id}`} className="magazine-readmore">Read more</a>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlogList;
