import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import BlogList from './components/Blog/BlogList';
import BlogForm from './components/Blog/BlogForm';
import BlogDetail from './components/Blog/BlogDetail';
import ProfileView from './components/Profile/ProfileView';
import './App.css';

function App() {
  return (
    <Router>
      <header className="header" style={{ background: 'linear-gradient(90deg, #6a11cb 0%, #2575fc 100%)', color: 'white', padding: '1rem 2rem', marginBottom: '2rem' }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', maxWidth: '1200px', margin: '0 auto', width: '100%' }}>
          <div style={{ fontWeight: 'bold', fontSize: '2rem', letterSpacing: '1px' }}>
            <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>SideBlogger</Link>
          </div>
          <nav className="nav-links" style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
            <Link to="/">Home</Link>
            <Link to="/create">Create Post</Link>
            <Link to="/profile">Profile</Link>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
            <Link to="#" style={{ fontWeight: 'bold' }}>Tech</Link>
            <Link to="#" style={{ fontWeight: 'bold' }}>Lifestyle</Link>
            <Link to="#" style={{ fontWeight: 'bold' }}>Education</Link>
            <Link to="#" style={{ fontWeight: 'bold' }}>Business</Link>
            <input type="text" placeholder="Search..." style={{ padding: '0.5rem', borderRadius: '4px', border: 'none', marginLeft: '1rem' }} />
          </nav>
        </div>
      </header>
      <Routes>
        <Route path="/" element={<BlogList />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/create" element={<BlogForm />} />
        <Route path="/edit/:id" element={<BlogForm />} />
        <Route path="/post/:id" element={<BlogDetail />} />
        <Route path="/profile" element={<ProfileView />} />
      </Routes>
    </Router>
  );
}

export default App;
