import React, { useEffect, useState } from 'react';
import { fetchUserProfile } from '../../utils/api';

function ProfileView({ userId }) {
  const [profile, setProfile] = useState(null);
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    async function getProfile() {
      try {
        const response = await fetchUserProfile(userId);
        setProfile(response.profile);
        setPosts(response.posts);
      } catch (err) {
        setError(err.message || 'Failed to load profile');
      }
    }
    getProfile();
  }, [userId]);

  if (error) return <div>Error: {error}</div>;
  if (!profile) return <div>Loading...</div>;

  return (
    <div className="container" style={{ maxWidth: 500, margin: '2rem auto' }}>
      <div className="blog-card" style={{ textAlign: 'center', padding: '2rem' }}>
        <img src={profile.profilePicture} alt="Profile" style={{ width: 120, height: 120, borderRadius: '50%', marginBottom: '1rem', objectFit: 'cover', boxShadow: '0 2px 8px rgba(106,17,203,0.08)' }} />
        <h2 style={{ marginBottom: '0.5rem' }}>{profile.name}</h2>
        <p style={{ color: '#666', marginBottom: '1rem' }}>{profile.bio}</p>
        <h3 style={{ marginTop: '2rem' }}>Posts</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {posts.map(post => (
            <li key={post._id} style={{ marginBottom: '0.5rem' }}>
              <span style={{ fontWeight: 'bold' }}>{post.title}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default ProfileView;
