import { useEffect, useState } from 'react';
import { fetchUserProfile, deletePost } from '../../utils/api';

function MyPosts() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [confirmId, setConfirmId] = useState(null);
  const [confirmStep, setConfirmStep] = useState(0);

  useEffect(() => {
    async function getProfile() {
      try {
        const response = await fetchUserProfile();
        setPosts(response.posts);
      } catch (err) {
        setError(err.message || 'Failed to load posts');
      } finally {
        setLoading(false);
      }
    }
    getProfile();
  }, []);

  const handleDelete = async (postId) => {
    if (confirmStep === 0) {
      setConfirmId(postId);
      setConfirmStep(1);
      return;
    }
    if (confirmStep === 1 && confirmId === postId) {
      setConfirmStep(2);
      return;
    }
    if (confirmStep === 2 && confirmId === postId) {
      const token = localStorage.getItem('token');
      await deletePost(postId, token);
      setPosts(posts.filter(p => p._id !== postId));
      setConfirmId(null);
      setConfirmStep(0);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="container" style={{ maxWidth: 600, margin: '2rem auto' }}>
      <div className="blog-card" style={{ padding: '2rem' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '1.5rem' }}>My Posts</h2>
        {posts.length === 0 ? (
          <div style={{ textAlign: 'center', color: '#888', fontSize: '1.1rem' }}>No posts available</div>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {posts.map(post => (
              <li key={post._id} style={{ marginBottom: '1.5rem', borderBottom: '1px solid #eee', paddingBottom: '1rem' }}>
                <div style={{ fontWeight: 'bold', fontSize: '1.1rem', marginBottom: '0.5rem' }}>{post.title}</div>
                <div style={{ color: '#666', marginBottom: '0.5rem' }}>{post.content.substring(0, 100)}...</div>
                <button
                  onClick={() => handleDelete(post._id)}
                  style={{ background: '#ff4d4f', color: 'white', border: 'none', borderRadius: '24px', padding: '0.5rem 1.2rem', fontWeight: 'bold', fontSize: '1rem', cursor: 'pointer' }}
                >
                  {confirmId === post._id && confirmStep === 1 ? 'Confirm Delete' : confirmId === post._id && confirmStep === 2 ? 'Are you sure? Click again' : 'Delete'}
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default MyPosts;
