import axios from 'axios';
import { useEffect, useState } from 'react';

const API_URL = 'http://localhost:5000/api/posts';

const BlogList = () => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await axios.get(API_URL);
                setPosts(response.data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };
        fetchPosts();
    }, []);

    if (loading) return <div className="container">Loading...</div>;
    if (error) return <div className="container">Error: {error}</div>;

    return (
        <div className="container magazine-layout">
            <h1 className="magazine-title">Latest & Trending</h1>
            <div className="magazine-list">
                {posts.map(post => (
                    <div className="magazine-row-card" key={post._id} style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', marginBottom: '2rem', background: '#fff', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)', padding: '1.5rem' }}>
                        <div className="magazine-content" style={{ flex: 1 }}>
                            <div className="magazine-title" style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '0.5rem' }}>{post.title}</div>
                            <div className="magazine-meta" style={{ color: '#888', marginBottom: '0.75rem' }}>By {post.author?.name || 'Unknown'} | {new Date(post.createdAt).toLocaleDateString()}</div>
                            <p className="magazine-excerpt" style={{ marginBottom: '1rem' }}>{post.content.substring(0, 180)}...</p>
                            <a href={`/post/${post._id}`} className="magazine-readmore" style={{ background: 'linear-gradient(90deg, #6a11cb 0%, #2575fc 100%)', color: 'white', padding: '0.5rem 1.5rem', borderRadius: '4px', textDecoration: 'none', fontWeight: 'bold', fontSize: '1rem' }}>Read More</a>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlogList;
