import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { createPost, updatePost, getPost } from '../../utils/api';

const BlogForm = () => {
    const { id } = useParams();
    const history = useNavigate();
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [tags, setTags] = useState('');
    const [image, setImage] = useState(null);
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        if (id) {
            setIsEditing(true);
            getPost(id).then(post => {
                setTitle(post.title);
                setContent(post.content);
                setTags(post.tags.join(', '));
            });
        }
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const postData = { title, content, tags: tags.split(',').map(tag => tag.trim()), image };

        if (isEditing) {
            await updatePost(id, postData);
        } else {
            await createPost(postData);
        }
        history.push('/'); // Redirect to home after submission
    };

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Title:</label>
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
            </div>
            <div>
                <label>Content:</label>
                <textarea value={content} onChange={(e) => setContent(e.target.value)} required />
            </div>
            <div>
                <label>Tags:</label>
                <input type="text" value={tags} onChange={(e) => setTags(e.target.value)} />
            </div>
            <div>
                <label>Image:</label>
                <input type="file" onChange={handleImageChange} />
            </div>
            <button type="submit">{isEditing ? 'Update Post' : 'Create Post'}</button>
        </form>
    );
};

export default BlogForm;
