import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { createPost, updatePost, getPost } from '../../utils/api';

const BlogForm = () => {
    const { id } = useParams();
    const history = useNavigate();
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [tags, setTags] = useState('');
    const [image, setImage] = useState(null);
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        if (id) {
            setIsEditing(true);
            getPost(id).then(post => {
                setTitle(post.title);
                setContent(post.content);
                setTags(post.tags.join(', '));
            });
        }
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const postData = { title, content, tags: tags.split(',').map(tag => tag.trim()), image };

        if (isEditing) {
            await updatePost(id, postData);
        } else {
            await createPost(postData);
        }
        history.push('/'); // Redirect to home after submission
    };

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    return (
        <div className="container" style={{ maxWidth: 500, margin: '2rem auto' }}>
            <div className="blog-card" style={{ padding: '2rem' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '1.5rem' }}>{isEditing ? 'Edit Post' : 'Create Post'}</h2>
                <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
                    <div>
                        <label style={{ display: 'block', marginBottom: '0.5rem' }}>Title:</label>
                        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required style={{ width: '100%', padding: '0.5rem', borderRadius: '4px', border: '1px solid #ccc' }} />
                    </div>
                    <div>
                        <label style={{ display: 'block', marginBottom: '0.5rem' }}>Content:</label>
                        <textarea value={content} onChange={(e) => setContent(e.target.value)} required style={{ width: '100%', padding: '0.5rem', borderRadius: '4px', border: '1px solid #ccc', minHeight: '120px' }} />
                    </div>
                    <div>
                        <label style={{ display: 'block', marginBottom: '0.5rem' }}>Tags:</label>
                        <input type="text" value={tags} onChange={(e) => setTags(e.target.value)} style={{ width: '100%', padding: '0.5rem', borderRadius: '4px', border: '1px solid #ccc' }} />
                    </div>
                    <div>
                        <label style={{ display: 'block', marginBottom: '0.5rem' }}>Image:</label>
                        <input type="file" onChange={handleImageChange} />
                        {image && (
                            <img src={URL.createObjectURL(image)} alt="Preview" style={{ width: '100%', borderRadius: '8px', marginTop: '1rem' }} />
                        )}
                    </div>
                    <button type="submit" style={{ background: 'linear-gradient(90deg, #6a11cb 0%, #2575fc 100%)', color: 'white', border: 'none', borderRadius: '4px', padding: '0.75rem', fontWeight: 'bold', cursor: 'pointer' }}>{isEditing ? 'Update Post' : 'Create Post'}</button>
                </form>
            </div>
        </div>
    );
};

export default BlogForm;
