import axios from 'axios';
import { useEffect, useState } from 'react';


const BlogList = () => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await axios.get('/api/posts');
                setPosts(response.data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };
        fetchPosts();
    }, []);

    if (loading) return <div className="container">Loading...</div>;
    if (error) return <div className="container">Error: {error}</div>;

    return (
        <div className="container">
            <h1 style={{ textAlign: 'center', marginBottom: '2rem' }}>Blog Posts</h1>
            <div className="blog-list">
                {posts.map(post => (
                    <div className="blog-card" key={post._id}>
                        {post.image && (
                            <img src={post.image} alt="Blog" style={{ width: '100%', borderRadius: '8px', marginBottom: '1rem' }} />
                        )}
                        <div className="blog-title">{post.title}</div>
                        <div className="blog-meta">By {post.author?.name || 'Unknown'} | {new Date(post.createdAt).toLocaleDateString()}</div>
                        <p>{post.content.substring(0, 120)}...</p>
                        <a href={`/post/${post._id}`} style={{ color: '#2575fc', textDecoration: 'underline', fontWeight: 'bold' }}>Read more</a>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlogList;
