import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const BlogDetail = () => {
    const { id } = useParams();
    const [post, setPost] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await axios.get(`/api/posts/${id}`);
                setPost(response.data);
            } catch (err) {
                setError('Error fetching post details');
            } finally {
                setLoading(false);
            }
        };

        fetchPost();
    }, [id]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="container" style={{ maxWidth: 600, margin: '2rem auto' }}>
            <div className="blog-card">
                {post.image && (
                    <img src={post.image} alt="Blog" style={{ width: '100%', borderRadius: '8px', marginBottom: '1rem' }} />
                )}
                <div className="blog-title" style={{ fontSize: '2rem' }}>{post.title}</div>
                <div className="blog-meta">By {post.author?.name || 'Unknown'} | {new Date(post.createdAt).toLocaleDateString()}</div>
                <p style={{ margin: '1rem 0' }}>{post.content}</p>
                <div style={{ marginBottom: '1rem', color: '#2575fc' }}>
                    Tags: {post.tags?.join(', ') || 'None'}
                </div>
            </div>
        </div>
    );
};

export default BlogDetail;
