import { useState } from 'react';
import { Link, Navigate, Route, BrowserRouter as Router, Routes, useLocation, useNavigate } from 'react-router-dom';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import BlogDetail from './components/Blog/BlogDetail';
import BlogForm from './components/Blog/BlogForm';
import BlogList from './components/Blog/BlogList';
import ProfileView from './components/Profile/ProfileView';
  function AppContent() {
  const [search, setSearch] = useState('');
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
    const navigate = useNavigate();
    const location = useLocation();
    const isLoggedIn = !!localStorage.getItem('token');

    const handleSearch = (e) => {
      e.preventDefault();
      if (search.trim()) {
        navigate(`/search/${search.trim()}`);
      }
    };

    const handleLogout = () => {
      localStorage.removeItem('token');
      navigate('/login');
    };

    // Redirect to login if not logged in and not on register page
    if (!isLoggedIn && location.pathname !== '/login' && location.pathname !== '/register') {
      return <Navigate to="/login" replace />;
    }

    return (
      <>
        <header className="header" style={{ background: 'linear-gradient(90deg, #6a11cb 0%, #2575fc 100%)', color: 'white', padding: '1rem 2rem', marginBottom: '2rem' }}>
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', maxWidth: '1200px', margin: '0 auto', width: '100%' }}>
            <div style={{ fontWeight: 'bold', fontSize: '2rem', letterSpacing: '1px' }}>
              <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>SideBlogger</Link>
            </div>
            <nav className="nav-links" style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
              {isLoggedIn && <Link to="/">Home</Link>}
              {isLoggedIn && <Link to="/create">Create Post</Link>}
              {!isLoggedIn && <Link to="/login">Login</Link>}
              {!isLoggedIn && <Link to="/register">Register</Link>}
              {isLoggedIn && (
                <div style={{ position: 'relative' }}>
                  <button
                    style={{ background: 'transparent', color: 'white', border: 'none', fontWeight: 'bold', cursor: 'pointer', padding: '0.5rem 1rem', borderRadius: '4px' }}
                    onClick={e => {
                      e.preventDefault();
                      setShowProfileDropdown(prev => !prev);
                    }}
                  >
                    Profile ▾
                  </button>
                  {showProfileDropdown && (
                    <div style={{ position: 'absolute', right: 0, top: '100%', background: 'white', color: '#333', boxShadow: '0 2px 8px rgba(0,0,0,0.15)', borderRadius: '4px', minWidth: '160px', zIndex: 10 }}>
                      <Link to="/profile" style={{ display: 'block', padding: '0.75rem 1rem', textDecoration: 'none', color: '#333', borderBottom: '1px solid #eee' }} onClick={() => setShowProfileDropdown(false)}>Profile</Link>
                      <Link to="/my-posts" style={{ display: 'block', padding: '0.75rem 1rem', textDecoration: 'none', color: '#333', borderBottom: '1px solid #eee' }} onClick={() => setShowProfileDropdown(false)}>My Posts</Link>
                      <button onClick={() => { setShowProfileDropdown(false); handleLogout(); }} style={{ display: 'block', width: '100%', padding: '0.75rem 1rem', background: 'none', border: 'none', textAlign: 'left', color: '#333', cursor: 'pointer' }}>Logout</button>
                    </div>
                  )}
                </div>
              )}
              <form onSubmit={handleSearch} style={{ display: 'inline' }}>
                <input type="text" value={search} onChange={e => setSearch(e.target.value)} placeholder="Search..." style={{ padding: '0.5rem', borderRadius: '4px', border: 'none', marginLeft: '1rem' }} />
              </form>
            </nav>
          </div>
        </header>
        <Routes>
          <Route path="/" element={isLoggedIn ? <BlogList /> : <Navigate to="/login" replace />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/create" element={isLoggedIn ? <BlogForm /> : <Navigate to="/login" replace />} />
          <Route path="/edit/:id" element={isLoggedIn ? <BlogForm /> : <Navigate to="/login" replace />} />
          <Route path="/post/:id" element={<BlogDetail />} />
            <Route path="/my-posts" element={isLoggedIn ? <MyPosts /> : <Navigate to="/login" replace />} />
          <Route path="/profile" element={isLoggedIn ? <ProfileView /> : <Navigate to="/login" replace />} />
          {/* Add category and search routes here for future expansion */}
        </Routes>
      </>
    );
  }

    import MyPosts from './components/Profile/MyPosts';
  function App() {
    return (
      <Router>
        <AppContent />
      </Router>
    );
  }

  export default App;
